import 'package:flutter/material.dart';import '../../core/utils/resource/colors.dart';import '../../core/utils/resource/styles.dart';ThemeData buildThemeDataDark(context) {  return ThemeData(    appBarTheme: AppBarTheme(      surfaceTintColor: AppColors.backGroundColor,      scrolledUnderElevation: 0,      centerTitle: true,      titleTextStyle: Styles.text20AppBar(context),      backgroundColor: AppColors.backGroundColor,    ),    datePickerTheme: DatePickerThemeData(        confirmButtonStyle: ButtonStyle(            textStyle: WidgetStatePropertyAll(Styles.text16(context)),            backgroundColor: WidgetStatePropertyAll(AppColors.primaryColor))),    scaffoldBackgroundColor: AppColors.backGroundColor,  );}