import 'dart:math';import 'package:hive/hive.dart';import 'package:uuid/uuid.dart';import '../../features/AddTask/model/category_model.dart';import '../../features/AddTask/model/priority_model.dart';part 'task_model.g.dart';@HiveType(typeId: 3)class TaskModel extends HiveObject {  @HiveField(0)  final String id;  @HiveField(1)  final String title;  @HiveField(2)  final String description;  @HiveField(3)  final DateTime dateTime;  @HiveField(4)  bool isCompleted;  @HiveField(5)  final CategoryModel type;  @HiveField(6)  final PriorityModel priority;  @HiveField(7)  final int idNotification;  TaskModel(      {String? id,      required this.title,      required this.dateTime,      required this.type,      required this.priority,      required this.description,      this.isCompleted = false,      int? idNotification})      : id = id ?? const Uuid().v4(),        idNotification =            idNotification ?? generateRandomNumberWithRandomDigitCount();  Map<String, dynamic> toMap() {    return {      'id': id,      'title': title,      'description': description,      'dateTime': dateTime,      'isCompleted': isCompleted,      'type': type,      'priority': priority,    };  }  factory TaskModel.fromMap(Map<String, dynamic> map) {    return TaskModel(      id: map["id"],      title: map['title'],      description: map['description'],      dateTime: map['dateTime'],      type: map['type'],      priority: map['priority'],    );  }}int generateRandomNumberWithRandomDigitCount() {  Random random = Random();  int digitCount = random.nextInt(5) + 1;  int min = pow(10, digitCount - 1).toInt();  int max = pow(10, digitCount).toInt() - 1;  return min + random.nextInt(max - min + 1);}