import 'package:hive/hive.dart';import '../../features/TaskManagement/model/category_model.dart';import '../../features/TaskManagement/model/priority_model.dart';part 'task_model.g.dart'; // Generate the part file for adapters@HiveType(typeId: 3)class TaskModel extends HiveObject {  @HiveField(0)  final String title;  @HiveField(1)  final String description;  @HiveField(2)  final DateTime dateTime;  @HiveField(3)  bool isCompleted;  @HiveField(4)  final CategoryModel type;  @HiveField(5)  final PriorityModel priority;  TaskModel(    this.title,    this.dateTime,    this.type,    this.priority,    this.description, {    this.isCompleted = false,  });  Map<String, dynamic> toMap() {    return {      'title': title,      'description': description,      'dateTime': dateTime,      'isCompleted': isCompleted,      'type': type,      'priority': priority,    };  }  factory TaskModel.fromMap(Map<String, dynamic> map) {    return TaskModel(      map['title'],      map['description'],      map['dateTime'],      map['type'],      map['priority'],    );  }}