import 'package:flutter/material.dart';import '../../../../core/utils/resource/styles.dart';class BuildCustomBox extends StatelessWidget {  final void Function() onTapChange;  final Color backGround;  final Icon icon;  final String? title;  final String? textIcon;  final double? heightContainer;  final double? widthContainer;  const BuildCustomBox({    super.key,    required this.onTapChange,    required this.backGround,    required this.icon,    this.title,    this.textIcon,    this.heightContainer,    this.widthContainer,  });  @override  Widget build(BuildContext context) {    return GestureDetector(        onTap: onTapChange,        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            Container(              height:                  MediaQuery.sizeOf(context).height * (heightContainer ?? .089),              width: MediaQuery.sizeOf(context).width * (widthContainer ?? .2),              decoration: BoxDecoration(                color: backGround,              ),              child: Align(                alignment: Alignment.center,                child: Column(                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,                  children: [                    icon,                    if (textIcon != null)                      Text(                        textIcon!,                        style: Styles.text14()                            .copyWith(fontWeight: FontWeight.w400),                      )                  ],                ),              ),            ),            if (title != null)              Text(                title!,                style: Styles.text14().copyWith(fontWeight: FontWeight.w400),              )          ],        ));  }}