import 'package:flutter/material.dart';import 'package:cached_network_image/cached_network_image.dart';const Icon buildIcon = Icon(  Icons.warning,  color: Colors.yellow,);class BuildImageCash extends StatelessWidget {  final String urlImage;  const BuildImageCash({super.key, required this.urlImage});  @override  Widget build(BuildContext context) {    return CachedNetworkImage(      filterQuality: FilterQuality.high,      imageUrl: urlImage,      placeholder: (context, url) =>          Center(child: const CircularProgressIndicator()),      errorWidget: (context, url, error) => Center(child: buildIcon),    );  }}