import 'package:flutter/material.dart';import '../resource/icons.dart';import '../../../features/Calendar/data/models/task_model.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';class BuildItemsTask extends StatefulWidget {  final List<TaskModel> items;  const BuildItemsTask({    super.key,    required this.items,  });  @override  State<BuildItemsTask> createState() => _BuildItemsTaskState();}class _BuildItemsTaskState extends State<BuildItemsTask> {  @override  void initState() {    // TODO: implement initState    super.initState();  }  @override  Widget build(BuildContext context) {    return ListView.builder(        physics: const NeverScrollableScrollPhysics(),        shrinkWrap: true,        itemCount: widget.items.length,        itemBuilder: (context, index) {          return Card(            margin: const EdgeInsets.only(left: 24, right: 24, top: 17),            shape: const ContinuousRectangleBorder(),            color: AppColors.secondBackGroundColor,            child: ListTile(              leading: AppIcons.circle,              title: Text(widget.items[index].title, style: Styles.text16()),              subtitle: Row(                crossAxisAlignment: CrossAxisAlignment.start,                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [                  Text("Today At ${widget.items[index].time}",                      style: Styles.text14()                          .copyWith(color: AppColors.gray1TextColor)),                  Row(                    crossAxisAlignment: CrossAxisAlignment.end,                    children: [                      if (widget.items[index].type == "University")                        Container(                          padding: const EdgeInsets.symmetric(horizontal: 8),                          color: AppColors.primaryColor,                          child: Row(                            children: [                              const Padding(                                padding: EdgeInsets.all(3.0),                                child: Icon(Icons.school_outlined,                                    color: Color(0xFF0055A3)),                              ),                              Text(                                "University",                                style: Styles.text12(),                              )                            ],                          ),                        ),                      if (widget.items[index].type == "Home")                        Container(                          padding: const EdgeInsets.symmetric(horizontal: 8),                          color: const Color(0xFFFF8080),                          child: Row(                            children: [                              const Padding(                                padding: EdgeInsets.all(3.0),                                child: Icon(Icons.home_outlined,                                    color: Color(0xFFA30000)),                              ),                              Text(                                "Home",                                style: Styles.text12(),                              )                            ],                          ),                        ),                      if (widget.items[index].type == "Work")                        Container(                          padding: const EdgeInsets.symmetric(horizontal: 8),                          color: const Color(0xFFFFCC80),                          child: Row(                            children: [                              const Padding(                                padding: EdgeInsets.all(3.0),                                child: Icon(Icons.work_outline_outlined,                                    color: Color(0xFFA36200)),                              ),                              Text(                                "Work",                                style: Styles.text12(),                              )                            ],                          ),                        ),                      const SizedBox(                        width: 12,                      ),                      Container(                        padding: const EdgeInsets.symmetric(                            horizontal: 8, vertical: 3),                        decoration: BoxDecoration(                          color: Colors.transparent,                          border: Border.all(                              width: 1, color: AppColors.primaryColor),                        ),                        child: Row(                          children: [                            const Padding(                              padding: EdgeInsets.symmetric(horizontal: 3.0),                              child: Icon(Icons.flag_outlined,                                  color: Colors.white),                            ),                            Text(                              widget.items[index].flag.toString(),                              style: Styles.text12(),                            )                          ],                        ),                      ),                    ],                  )                ],              ),            ),          );        });  }}