import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../features/Calendar/presentation/manager/calendar_cubit.dart';import '../../../../core/utils/resource/styles.dart';import '../../../features/Calendar/presentation/widgets/build_task.dart';class BuildItemsTask extends StatelessWidget {  const BuildItemsTask({    super.key,  });  @override  Widget build(BuildContext context) {    return BlocBuilder<CalendarCubit, CalendarState>(      builder: (context, state) {        if (state is CalendarLoading) {          return const Center(            child: CircularProgressIndicator(              color: Colors.white,            ),          );        } else if (state is CalendarSucceed) {          return ListView.builder(              physics: BouncingScrollPhysics(),              shrinkWrap: true,              itemCount: state.model.length,              itemBuilder: (context, index) {                return CardItemTask(                  state: state.model[index],                );              });        } else {          return Center(            child: Text(              "NO Task",              style: Styles.text16(),            ),          );        }      },    );  }}