import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:intl/intl.dart';import 'package:uptodo/core/utils/resource/constant.dart';import '../../../features/task/View/Pages/task_screen.dart';import '../../model/task_model.dart';import '../resource/icons.dart';import '../../../features/TaskManagement/ViewModel/AddTask/add_task_cubit.dart';import '../resource/colors.dart';import '../resource/styles.dart';class CardItemTask extends StatelessWidget {  final TaskModel state;  const CardItemTask({    super.key,    required this.state,  });  @override  Widget build(BuildContext context) {    return Card(      margin: const EdgeInsets.only(left: 24, right: 24, top: 17),      shape: const ContinuousRectangleBorder(),      color: AppColors.secondBackGroundColor,      child: InkWell(        onTap: () {          Navigator.of(context).push(MaterialPageRoute(            builder: (context) => TaskScreen(              model: state,            ),          ));        },        child: ListTile(          leading: IconButton(              onPressed: () {                state.isCompleted = !state.isCompleted;                context.read<AddTaskCubit>().filter();                context.read<AddTaskCubit>().update(state);              },              icon:                  !state.isCompleted ? AppIcons.circle : AppIcons.checkCircle),          title: Text(state.title, maxLines: 1, style: Styles.text16()),          contentPadding: EdgeInsets.zero,          subtitle: Row(            crossAxisAlignment: CrossAxisAlignment.start,            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              Expanded(                  child: Text(                "Today At ${DateFormat.jm().format(state.dateTime)}",                maxLines: 1,                style:                    Styles.text14().copyWith(color: AppColors.gray1TextColor),              )),              Row(                crossAxisAlignment: CrossAxisAlignment.end,                children: [                  Container(                    padding: const EdgeInsets.symmetric(horizontal: 8),                    color: state.type.backGroundColor,                    child: Row(                      children: [                        Padding(                          padding: const EdgeInsets.all(3.0),                          child: Icon(                            IconData(state.type.icon,                                fontFamily: AppConstant.kFontIcon),                            color: state.type.colorIcon,                          ),                        ),                        Text(                          state.type.name,                          style: Styles.text12(),                        )                      ],                    ),                  ),                  const SizedBox(                    width: 12,                  ),                  Container(                    padding:                        const EdgeInsets.symmetric(horizontal: 8, vertical: 3),                    decoration: BoxDecoration(                      color: Colors.transparent,                      border:                          Border.all(width: 1, color: AppColors.primaryColor),                    ),                    child: Row(                      children: [                        Padding(                          padding: const EdgeInsets.symmetric(horizontal: 3.0),                          child: Icon(state.priority.flag,                              color: AppColors.iconColor),                        ),                        Text(                          state.priority.number.toString(),                          style: Styles.text12(),                        )                      ],                    ),                  ),                ],              )            ],          ),        ),      ),    );  }}