import 'package:flutter/material.dart';import '../resource/styles.dart';class BuildTextFormField extends StatelessWidget {  final TextEditingController controller;  final String? hint;  final FocusNode? focusNode;  final bool? obscureText;  const BuildTextFormField({    super.key,    required this.controller,    this.hint,    this.focusNode,    this.obscureText,  });  @override  Widget build(BuildContext context) {    return TextFormField(      focusNode: focusNode,      validator: (value) {        obscureText ?? false;        return obscureText!            ? _customValidate(value: value.toString(), minLength: 6)            : _customValidate(value: value.toString(), minLength: 2);      },      controller: controller,      style: Styles.text18(),      obscureText: obscureText ?? false,      decoration: InputDecoration(        hintText: hint,        border: const OutlineInputBorder(),      ),    );  }}String? _customValidate({  required String value,  String? titleCheck,  bool trimWhitespace = true,  int? minLength,  String? emptyMessage,  String? minLengthMessage,}) {  final String p0 = titleCheck ?? "Field";  final String trimmedValue = trimWhitespace ? value.trim() : value;  if (minLength == null && trimmedValue.isEmpty) {    return emptyMessage ?? "Please enter this $p0.";  }  if (minLength != null && trimmedValue.length < minLength) {    return minLengthMessage ??        "$p0 must be at least $minLength characters long.";  }  return null;}