import 'package:flutter/material.dart';import '../resource/styles.dart';class BuildTextFormField extends StatelessWidget {  final TextEditingController title;  final String hint;  const BuildTextFormField({    super.key,    required this.title,    required this.hint,  });  @override  Widget build(BuildContext context) {    return TextFormField(      validator: (value) =>          _customValidate(value: value.toString(), minLength: 2),      controller: title,      style: Styles.text18(),      decoration: InputDecoration(        hintText: hint,        border: const OutlineInputBorder(),      ),    );  }}String? _customValidate({  required String value,  String? titleCheck,  bool trimWhitespace = true,  int? minLength,  String? emptyMessage,  String? minLengthMessage,}) {  final String p0 = titleCheck ?? "Field";  final String trimmedValue = trimWhitespace ? value.trim() : value;  if (minLength == null && trimmedValue.isEmpty) {    return emptyMessage ?? "Please enter this $p0.";  }  if (minLength != null && trimmedValue.length < minLength) {    return minLengthMessage ??        "$p0 must be at least $minLength characters long.";  }  return null;}