import 'package:flutter/material.dart';import '../resource/size.dart';import '../resource/colors.dart';class CustomBuildDialog extends StatelessWidget {  final NullableIndexedWidgetBuilder itemBuilder;  final int count;  final Widget? child;  final double? heightContainer;  final Color? backGround;  final double? widthContainer;  const CustomBuildDialog({    super.key,    required this.itemBuilder,    required this.count,    this.heightContainer,    this.backGround,    this.widthContainer,    this.child,  });  @override  Widget build(BuildContext context) {    final AppSize size = AppSize(context);    return Dialog(      alignment: Alignment.center,      insetPadding: const EdgeInsets.all(0),      child: Container(        decoration: BoxDecoration(          color: backGround ?? AppColors.secondBackGroundColor,          borderRadius: BorderRadius.circular(12.0),        ),        height: size.percentageHeight(heightContainer ?? 0.55),        width: size.percentageWidth(widthContainer ?? 0.8),        margin: const EdgeInsets.all(0),        child: Column(          children: [            Expanded(              child: GridView.builder(                physics: const ClampingScrollPhysics(),                shrinkWrap: true,                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(                  crossAxisCount: size.crossAxisCount,                  childAspectRatio: 1,                ),                itemCount: count,                itemBuilder: itemBuilder,              ),            ),            if (child != null) ...[              child!,              SizedBox(                height: 15,              )            ],          ],        ),      ),    );  }}