import 'package:flutter/material.dart';import '../resource/colors.dart';class CustomBuildDialog extends StatelessWidget {  final NullableIndexedWidgetBuilder itemBuilder;  final int count;  final Widget? child;  final double? heightContainer;  final Color? backGround;  final double? widthContainer;  const CustomBuildDialog({    super.key,    required this.itemBuilder,    required this.count,    this.heightContainer,    this.backGround,    this.widthContainer,    this.child,  });  @override  Widget build(BuildContext context) {    return Dialog(      alignment: Alignment.center,      insetPadding: const EdgeInsets.all(0),      child: Container(        color: backGround ?? AppColors.secondBackGroundColor,        height: MediaQuery.of(context).size.height * (heightContainer ?? 0.55),        width: MediaQuery.of(context).size.width * (widthContainer ?? .8),        padding: const EdgeInsets.all(10.0),        child: Column(          children: [            GridView.builder(              shrinkWrap: true,              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(                crossAxisCount: 3,              ),              itemCount: count,              itemBuilder: itemBuilder,            ),            child ?? SizedBox()          ],        ),      ),    );  }}