import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:uptodo/core/utils/resource/constant.dart';import 'package:uptodo/core/utils/resource/styles.dart';import '../resource/size.dart';import '../resource/colors.dart';class CustomBuildDialog extends StatelessWidget {  final NullableIndexedWidgetBuilder itemBuilder;  final int count;  final Widget? child;  final double? heightContainer;  final Color? backGround;  final double? widthContainer;  const CustomBuildDialog({    super.key,    required this.itemBuilder,    required this.count,    this.heightContainer,    this.backGround,    this.widthContainer,    this.child,  });  @override  Widget build(BuildContext context) {    return Dialog(      alignment: Alignment.center,      insetPadding: EdgeInsets.zero,      child: Container(        decoration: BoxDecoration(          color: backGround ?? AppColors.secondBackGroundColor,          borderRadius: BorderRadius.circular(12.0).r,        ),        height:(heightContainer?? 327).w,        width: 327.w,        margin: EdgeInsets.zero,        child: Column(          children: [            Expanded(              child: GridView.builder(                physics: AppConstant.physics,                shrinkWrap: true,                padding: EdgeInsets.zero,                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(                  crossAxisCount: (64.w ~/ 150).clamp(heightContainer !=null? 3:4, 6),                ),                itemCount: count,                itemBuilder: itemBuilder,              ),            ),            if (child != null) ...[              5.verticalSpace,              child!,              5.verticalSpace            ],          ],        ),      ),    );  }}