import 'package:flutter/material.dart';import '../resource/colors.dart';class CustomBuildDialog extends StatelessWidget {  final NullableIndexedWidgetBuilder itemBuilder;  final int count;  final double? heightContainer;  final Color? backGround;  final double? widthContainer;  const CustomBuildDialog({    super.key,    required this.itemBuilder,    required this.count,    this.heightContainer,    this.backGround,    this.widthContainer,  });  @override  Widget build(BuildContext context) {    return Dialog(      insetPadding: const EdgeInsets.all(0),      child: Container(        color: backGround ?? AppColors.secondBackGroundColor,        height: MediaQuery.of(context).size.height * (heightContainer ?? 0.4),        width: MediaQuery.of(context).size.width * (widthContainer ?? .8),        padding: const EdgeInsets.all(10.0),        child: GridView.builder(          gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(            crossAxisCount: 3,          ),          itemCount: count,          itemBuilder: itemBuilder,        ),      ),    );  }}