import 'package:flutter/material.dart';import 'package:fluttertoast/fluttertoast.dart';void showMessage({  required String text,  required ToastStates state,}) {  Fluttertoast.showToast(    msg: text,    toastLength: Toast.LENGTH_SHORT,    gravity: ToastGravity.BOTTOM,    timeInSecForIosWeb: 5,    backgroundColor: choseToastColor(state),    textColor: Colors.white,  );}enum ToastStates { error, warning, succeed }Color choseToastColor(ToastStates state) {  switch (state) {    case ToastStates.succeed:      return Colors.green;    case ToastStates.error:      return Colors.red;    case ToastStates.warning:      return Colors.amber;  }}