import 'package:flutter/material.dart';import '../../../../core/utils/resource/string.dart';import '../../../../core/utils/resource/styles.dart';import '../resource/colors.dart';import '../resource/size.dart';final ButtonStyle _buttonStyle = ButtonStyle(    shape: WidgetStatePropertyAll(        ContinuousRectangleBorder(borderRadius: BorderRadius.circular(6))),    backgroundColor: WidgetStatePropertyAll(AppColors.primaryColor));final TextStyle _defaultStyle16 = Styles.text16();class CustomRow extends StatelessWidget {  final bool showCancel;  final String name;  final double? height;  final void Function() onTap;  const CustomRow({    super.key,    required this.name,    required this.onTap,    this.showCancel = true,    this.height,  });  @override  Widget build(BuildContext context) {    final AppSize size = AppSize(context);    return Row(      mainAxisAlignment: MainAxisAlignment.spaceAround,      children: [        if (showCancel == true)          TextButton(            style: _buttonStyle.copyWith(                backgroundColor: WidgetStatePropertyAll(Colors.transparent)),            onPressed: () {              Navigator.pop(context);            },            child: Text(              AppStrings.cancel,              style: _defaultStyle16.copyWith(color: AppColors.primaryColor),            ),          ),        SizedBox(          width: size.percentageWidth(!showCancel ? .7 : .4),          height: size.percentageHeight(height ?? .06),          child: ElevatedButton(            clipBehavior: Clip.antiAliasWithSaveLayer,            style: _buttonStyle,            onPressed: onTap,            child: Text(              name,              style: _defaultStyle16,            ),          ),        ),      ],    );  }}