import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/utils/resource/string.dart';import '../../../../core/utils/resource/styles.dart';import '../resource/colors.dart';final ButtonStyle _buttonStyle = ButtonStyle(    shape: WidgetStatePropertyAll(        ContinuousRectangleBorder(borderRadius: BorderRadius.circular(6).r)),    backgroundColor: WidgetStatePropertyAll(AppColors.primaryColor));class CustomRow extends StatelessWidget {  final bool showCancel;  final String name;  final double? height;  final void Function() onTap;  const CustomRow({    super.key,    required this.name,    required this.onTap,    this.showCancel = true,    this.height,  });  @override  Widget build(BuildContext context) {    final TextStyle defaultStyle16 = Styles.text16();    return Row(      mainAxisAlignment: MainAxisAlignment.spaceAround,      children: [        if (showCancel == true)          TextButton(            style: _buttonStyle.copyWith(                backgroundColor: WidgetStatePropertyAll(Colors.transparent)),            onPressed: () {              Navigator.pop(context);            },            child: Text(              AppStrings.cancel,              style: defaultStyle16.copyWith(color: AppColors.primaryColor),            ),          ),        SizedBox(          width: !showCancel? 289.w: 153.w ,          height: (height?? 48).h,          child: ElevatedButton(            clipBehavior: Clip.antiAliasWithSaveLayer,            style: _buttonStyle,            onPressed: onTap,            child: Text(              name,              style: defaultStyle16,            ),          ),        ),      ],    );  }}