import 'dart:io';import 'dart:ui';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class ImageView extends StatelessWidget {  final String photo;  const ImageView({super.key, required this.photo});  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.transparent,      body: SafeArea(          child: Stack(        children: [          GestureDetector(            onTap: () => Navigator.of(context).pop(),            child: BackdropFilter(              filter: ImageFilter.blur(sigmaX: 10, sigmaY: 10),              child: Container(                color: Colors.transparent,              ),            ),          ),          Center(            child: CircleAvatar(              radius: .4.sw,              backgroundImage:                  FileImage(File(photo)), // Same photo for the avatar            ),          ),        ],      )),    );  }}