import 'dart:io';import 'dart:ui';import 'package:flutter/material.dart';class ImageView extends StatelessWidget {  final String photo;  const ImageView({super.key, required this.photo});  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.transparent,      body: SafeArea(          child: Stack(        children: [          BackdropFilter(            filter: ImageFilter.blur(sigmaX: 10, sigmaY: 10),            child: Container(              color: Colors.transparent,            ),          ),          Center(            child: InkWell(              onTap: () => Navigator.of(context).pop(),              child: CircleAvatar(                radius: MediaQuery.sizeOf(context).width * .35,                backgroundImage:                    FileImage(File(photo)), // Same photo for the avatar              ),            ),          ),        ],      )),    );  }}