import 'package:flutter/material.dart';class AppSize {  final BuildContext context;  AppSize(this.context);  double get sizeDialogAddTask => height * 0.38;  double get sizeDialogUserChangeName => height * 0.38;  double get sizeIcon => height * 0.08;  double get width => MediaQuery.of(context).size.width;  double get height => MediaQuery.of(context).size.height;  int get crossAxisCount => ((width / 110).clamp(2, 5)).toInt();  double percentageWidth(double percentage) {    return width * percentage;  }  double percentageHeight(double percentage) {    return height * percentage;  }}