import 'package:flutter/material.dart';import '../ResponsiveAdaptive/responsive_text.dart';import 'colors.dart';class Styles {  static TextStyle _getTextStyle(      BuildContext context, double size, FontWeight weight, Color color) {    double finalFontSize = ScaleFactor().getResponsiveFontSize(size, context);    return TextStyle(      fontSize: finalFontSize,      color: color,      fontWeight: weight,      overflow: TextOverflow.ellipsis,    );  }  static TextStyle text20AppBar(BuildContext context) =>      _getTextStyle(context, 20, FontWeight.w400, AppColors.textColor);  static TextStyle text10(BuildContext context) =>      _getTextStyle(context, 10, FontWeight.w700, AppColors.textColor);  static TextStyle text12(BuildContext context) =>      _getTextStyle(context, 12, FontWeight.w400, AppColors.textColor);  static TextStyle text14(BuildContext context) =>      _getTextStyle(context, 14, FontWeight.w700, AppColors.textColor);  static TextStyle text16(BuildContext context) =>      _getTextStyle(context, 16, FontWeight.w400, AppColors.textColor);  static TextStyle text18(BuildContext context) =>      _getTextStyle(context, 18, FontWeight.w400, AppColors.textColor);  static TextStyle text24(BuildContext context) =>      _getTextStyle(context, 24, FontWeight.w400, AppColors.textColor);}