import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:uptodo/core/utils/resource/size.dart';import '../../../../core/utils/Widget/custom_row.dart';import '../../../../core/utils/Widget/build_custom_box.dart';import '../../../../core/utils/Widget/custom_build_dialog.dart';import '../../../../core/utils/resource/list.dart';import '../../model/category_model.dart';import '../../../../core/utils/resource/string.dart';class BuildCategory extends StatefulWidget {  const BuildCategory({    super.key,  });  @override  State<BuildCategory> createState() => _BuildCategoryState();}CategoryModel? _categoryModel;class _BuildCategoryState extends State<BuildCategory> {  @override  Widget build(BuildContext context) {    return CustomBuildDialog(      heightContainer: 520,      count: AppList.defaultCategory.length,      itemBuilder: (context, index) => BuildCustomBox(        onTapChange: () {          _categoryModel = AppList.defaultCategory[index];          Navigator.of(context).pop(_categoryModel);        },        backGround: AppList.defaultCategory[index].backGroundColor,        icon: Icon(          AppList.defaultCategory[index].iconData,          weight: (AppSize.sizeIcon).sw,          color: AppList.defaultCategory[index].colorIcon,        ),        title: AppList.defaultCategory[index].name,      ),      child: CustomRow(        name: AppStrings.edite,        onTap: () {},        showCancel: false,      ),    );  }}