import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:hive/hive.dart';import '../../../Index/ViewModel/GetTask/get_task_cubit.dart';import '../../../../core/Network/serves_locator.dart';import '../../../../core/model/task_model.dart';class AddTaskCubit extends Cubit<bool> {  AddTaskCubit() : super(true);  //* Add Tasks  void addTask(TaskModel model, BuildContext context) {    final box = getIt<Box<TaskModel>>();    box.add(model);    context.read<GetTaskCubit>().getAllTasks();  }}