import 'package:flutter_bloc/flutter_bloc.dart';class CalendarPickerCubit extends Cubit<DateTime?> {  CalendarPickerCubit() : super(null);  DateTime? foucDate;  void initializeFocusDate(DateTime? dateTime) {    final now = DateTime.now();    final firstDayOfMonthORToday = DateTime(now.year, now.month, now.day);    if (dateTime != null) {      if (dateTime.isAfter(firstDayOfMonthORToday) ||          dateTime.isAtSameMomentAs(firstDayOfMonthORToday)) {        foucDate = dateTime;      } else {        foucDate = firstDayOfMonthORToday;      }    } else {      foucDate = firstDayOfMonthORToday;    }    emit(foucDate);  }  void changeSelectedDayDate(DateTime selectedDay) {    foucDate = selectedDay;    emit(foucDate);  }}