class TimePickerState {  final int hour;  final int minute;  final String timeFormat;  TimePickerState({    required this.hour,    required this.minute,    required this.timeFormat,  });  TimePickerState copyWith({    int? hour,    int? minute,    String? timeFormat,  }) {    return TimePickerState(      hour: hour ?? this.hour,      minute: minute ?? this.minute,      timeFormat: timeFormat ?? this.timeFormat,    );  }}