import 'package:flutter/material.dart';import '../../../../core/utils/resource/constant.dart';import '../../../../core/utils/Widget/build_custom_box.dart';import '../../../../core/utils/Widget/custom_build_dialog.dart';import '../../data/models/category_model.dart';class BuildCategory extends StatelessWidget {  const BuildCategory({    super.key,  });  @override  Widget build(BuildContext context) {    return CustomBuildDialog(      itemBuilder: (context, index) => BuildCustomBox(        onTap: () {          // _categoryModel = CategoryModel.defaultCategory[index];          Navigator.of(context).pop(CategoryModel.defaultCategory[index]);        },        backGround: CategoryModel.defaultCategory[index].backGroundColor,        icon: Icon(          IconData(CategoryModel.defaultCategory[index].icon,              fontFamily: AppConstant.kFontIcon),          size: MediaQuery.sizeOf(context).width * .08,          color: CategoryModel.defaultCategory[index].colorIcon,        ),        title: CategoryModel.defaultCategory[index].name,      ),      count: CategoryModel.defaultCategory.length,    );  }}