import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:uptodo/features/Calendar/presentation/manager/calendar_cubit.dart';import '../../../../core/utils/Widget/custom_row.dart';import '../../../../core/utils/Widget/build_custom_box.dart';import '../../../../core/utils/Widget/custom_build_dialog.dart';import '../../../../core/utils/resource/colors.dart';import '../../data/models/priority_model.dart';import '../../../../core/utils/resource/string.dart';class BuildPriority extends StatefulWidget {  const BuildPriority({    super.key,  });  @override  State<BuildPriority> createState() => _BuildPriorityState();}class _BuildPriorityState extends State<BuildPriority> {  PriorityModel? _priorityModel;//  current  @override  Widget build(BuildContext context) {    return CustomBuildDialog(      itemBuilder: (context, index) {        return BuildCustomBox(          heightContainer: .1,          onTapChange: () {            context.read<CalendarCubit>().selected = index;            _priorityModel = PriorityModel(index + 1);            Navigator.of(context).pop(_priorityModel);          },          backGround: context.read<CalendarCubit>().selected == index              ? AppColors.primaryColor              : AppColors.backGroundColor,          icon: Icon(            Icons.flag_outlined,            weight: MediaQuery.sizeOf(context).width * .08,            color: AppColors.iconColor,          ),          textIcon: (index + 1).toString(),        );      },      count: 10,      child: CustomRow(        name: AppStrings.edite,        onTap: () {},      ),    );  }}