import 'package:flutter/material.dart';import '../../../../core/utils/Widget/build_custom_box.dart';import '../../../../core/utils/Widget/custom_build_dialog.dart';import '../../../../core/utils/resource/colors.dart';import '../../data/models/priority_model.dart';class BuildPriority extends StatelessWidget {  const BuildPriority({    super.key,  });  @override  Widget build(BuildContext context) {    return CustomBuildDialog(      itemBuilder: (context, index) {        return BuildCustomBox(          onTap: () {            Navigator.of(context).pop(PriorityModel(index + 1));          },          backGround: AppColors.backGroundColor,          icon: Icon(            Icons.flag_outlined,            size: MediaQuery.sizeOf(context).width * .09,            color: AppColors.iconColor,          ),          textIcon: (index + 1).toString(),        );      },      count: 10,      heightContainer: .5,    );  }}