import 'package:flutter/material.dart';import '../../../../core/utils/Widget/custom_row.dart';import '../../../../core/utils/Widget/build_custom_box.dart';import '../../../../core/utils/Widget/custom_build_dialog.dart';import '../../../../core/utils/resource/colors.dart';import '../../data/models/priority_model.dart';import '../../../../core/utils/resource/string.dart';class BuildPriority extends StatefulWidget {  const BuildPriority({    super.key,  });  @override  State<BuildPriority> createState() => _BuildPriorityState();}class _BuildPriorityState extends State<BuildPriority> {  int? selectedIndex;  PriorityModel? _priorityModel;  @override  Widget build(BuildContext context) {    return CustomBuildDialog(      itemBuilder: (context, index) {        return BuildCustomBox(          onTapChange: () {            setState(() {              selectedIndex = index;              _priorityModel = PriorityModel(index + 1);            });            Navigator.of(context).pop(_priorityModel);          },          backGround: selectedIndex == index              ? AppColors.primaryColor              : AppColors.backGroundColor,          icon: Icon(            Icons.flag_outlined,            size: MediaQuery.sizeOf(context).width * .09,            color: AppColors.iconColor,          ),          textIcon: (index + 1).toString(),        );      },      count: 10,      child: Expanded(          child: CustomRow(        name: AppStrings.edite,        onTap: () {},      )),    );  }}