import 'package:flutter/material.dart';import '../../../../core/utils/Widget/default_app_bar.dart';import '../../../../core/utils/resource/string.dart';import '../Widget/build_date_time_line.dart';import '../Widget/build_buttons_switch.dart';class CalendarScreen extends StatelessWidget {  const CalendarScreen({super.key});  @override  Widget build(BuildContext context) {    return buildScaffold();  }  Scaffold buildScaffold() {    return Scaffold(      appBar: defaultAppBar(title: AppStrings.calenderNamePage),      body: _buildBody(),    );  }  Column _buildBody() {    return const Column(      children: [        BuildDateTimeLine(),        BuildButtonsSwitch(),      ],    );  }}