import 'package:easy_date_timeline/easy_date_timeline.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:intl/intl.dart';import 'package:uptodo/features/TaskManagement/ViewModel/AddTask/add_task_cubit.dart';import '../../../../core/utils/resource/icons.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';String _formatMonth = DateFormat("MMMM").format(DateTime.now()).toUpperCase();String _formatYear = DateFormat("yyyy").format(DateTime.now());BorderRadiusGeometry _borderRadius = const BorderRadius.all(Radius.circular(4));class BuildDateTimeLine extends StatelessWidget {  const BuildDateTimeLine({    super.key,  });  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.only(        top: 15,      ),      color: AppColors.secondBackGroundColor,      child: Column(        children: [          Row(            mainAxisAlignment: MainAxisAlignment.spaceAround,            children: [              AppIcons.arrowBack,              Column(                children: [                  Text(                    _formatMonth,                    style:                        Styles.text14(context).copyWith(fontWeight: FontWeight.w400),                  ),                  Text(                    _formatYear,                    style: Styles.text12(context)                        .copyWith(color: AppColors.gray1TextColor),                  ),                ],              ),              AppIcons.arrowForward            ],          ),          EasyDateTimeLine(            timeLineProps:                const EasyTimeLineProps(separatorPadding: 11, vPadding: 10),            initialDate: DateTime.now(),            onDateChange: (selectedDate) {              context.read<AddTaskCubit>().changeDate(selectedDate);            },            headerProps: const EasyHeaderProps(showHeader: false),            dayProps: EasyDayProps(              height: MediaQuery.sizeOf(context).height * .09,              width: MediaQuery.sizeOf(context).width / 9,              borderColor: Colors.transparent,              todayStyle: DayStyle(                borderRadius: 4,                dayNumStyle: Styles.text14(context),                dayStrStyle:  Styles.text12(context).copyWith(fontWeight: FontWeight.w700),              ),              inactiveDayStyle: DayStyle(                decoration: BoxDecoration(                  color: AppColors.backGroundColorItemsCalender,                  borderRadius: _borderRadius,                ),                dayNumStyle: Styles.text14(context),                dayStrStyle:  Styles.text12(context).copyWith(fontWeight: FontWeight.w700),              ),              activeDayStyle: DayStyle(                decoration: BoxDecoration(                  color: AppColors.primaryColor,                  borderRadius: _borderRadius,                ),                dayNumStyle: Styles.text14(context),                dayStrStyle:  Styles.text12(context).copyWith(fontWeight: FontWeight.w700),              ),              dayStructure: DayStructure.dayStrDayNum,              todayHighlightStyle: TodayHighlightStyle.withBackground,            ),          ),        ],      ),    );  }} //resource