import 'package:easy_date_timeline/easy_date_timeline.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:intl/intl.dart';import 'package:uptodo/features/Index/ViewModel/GetTask/get_task_cubit.dart';import '../../../../core/utils/resource/icons.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';String _formatMonth = DateFormat("MMMM").format(DateTime.now()).toUpperCase();String _formatYear = DateFormat("yyyy").format(DateTime.now());BorderRadiusGeometry _borderRadius = const BorderRadius.all(Radius.circular(4));class BuildDateTimeLine extends StatelessWidget {  const BuildDateTimeLine({    super.key,  });  @override  Widget build(BuildContext context) {    return Container(      height: 107.h,      padding: const EdgeInsets.only(        top: 7,      ).r,      color: AppColors.secondBackGroundColor,      child: Column(        children: [          Row(            mainAxisAlignment: MainAxisAlignment.spaceAround,            children: [              AppIcons.arrowBack,              Column(                children: [                  Text(                    _formatMonth,                    style:                        Styles.text14().copyWith(fontWeight: FontWeight.w400),                  ),                  Text(                    _formatYear,                    style: Styles.text12()                        .copyWith(color: AppColors.gray1TextColor),                  ),                ],              ),              AppIcons.arrowForward            ],          ),          9.verticalSpace,          EasyDateTimeLine(            timeLineProps: const EasyTimeLineProps(separatorPadding: 11),            initialDate: DateTime.now(),            onDateChange: (selectedDate) {              context.read<GetTaskCubit>().changeDate(selectedDate);            },            headerProps: const EasyHeaderProps(showHeader: false),            dayProps: EasyDayProps(              height: 48.h,              width: 39.w,              borderColor: Colors.transparent,              todayStyle: DayStyle(                borderRadius: 4,                dayNumStyle: Styles.text14(),                dayStrStyle:                    Styles.text12().copyWith(fontWeight: FontWeight.w700),              ),              inactiveDayStyle: DayStyle(                decoration: BoxDecoration(                  color: AppColors.backGroundColorItemsCalender,                  borderRadius: _borderRadius,                ),                dayNumStyle: Styles.text14(),                dayStrStyle:                    Styles.text12().copyWith(fontWeight: FontWeight.w700),              ),              activeDayStyle: DayStyle(                decoration: BoxDecoration(                  color: AppColors.primaryColor,                  borderRadius: _borderRadius,                ),                dayNumStyle: Styles.text14(),                dayStrStyle:                    Styles.text12().copyWith(fontWeight: FontWeight.w700),              ),              dayStructure: DayStructure.dayStrDayNum,              todayHighlightStyle: TodayHighlightStyle.withBackground,            ),          ),        ],      ),    );  }} //resource