import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/utils/Widget/build_task.dart';import '../../../TaskManagement/ViewModel/TaskCubit/task_management_cubit.dart';import '../../../../../core/utils/resource/styles.dart';class BuildItemsTaskCalender extends StatelessWidget {  const BuildItemsTaskCalender({    super.key,  });  @override  Widget build(BuildContext context) {    return BlocBuilder<TaskManagementCubit, TaskManagementState>(      builder: (context, state) {        if (state is GetTasksSucceed) {          return ListView.builder(              physics: BouncingScrollPhysics(),              shrinkWrap: true,              itemCount: state.model.length,              itemBuilder: (context, index) {                return CardItemTask(                  state: state.model[index],                );              });        } else {          return Center(            child: Text(              "NO Task",              style: Styles.text16(context),            ),          );        }      },    );  }}