import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/model/task_model.dart';import '../../../../core/utils/Widget/build_task.dart';import '../../../Index/ViewModel/GetTask/get_task_cubit.dart';import '../../../../../core/utils/resource/styles.dart';class TaskCompletedBySelected extends StatelessWidget {  const TaskCompletedBySelected({    super.key,  });  @override  Widget build(BuildContext context) {    return BlocBuilder<GetTaskCubit, GetTaskState>(      builder: (context, state) {        return FutureBuilder<List<TaskModel>>(          future: Future.value(            context.read<GetTaskCubit>().selectTaskCompletedBySelectedDateTime(),          ),          builder: (context, snapshot) {            if (snapshot.hasData && snapshot.data!.isNotEmpty) {              final tasks = snapshot.data!;              return ListView.builder(                  physics: BouncingScrollPhysics(),                  shrinkWrap: true,                  itemCount: tasks.length,                  itemBuilder: (context, index) {                    if (tasks.isEmpty) {                      return Center(child: Text("No task"));                    }                    return TaskBuilder(                      state: tasks[index],                    );                  });            } else if (snapshot.hasError) {              return Center(                child: Text(                  "Error loading tasks",                  style: Styles.text16(),                ),              );            }            return Container();          },        );      },    );  }}