import 'package:flutter/material.dart';class CategoryModel {  final String name;  final IconData icon;  final Color colorIcon;  final Color backGroundColor;  CategoryModel(this.name, this.icon, this.colorIcon, this.backGroundColor);  // Factory method to create CategoryModel from JSON  factory CategoryModel.fromJson(Map<String, dynamic> json) {    return CategoryModel(      json['name'],      IconData(json['icon'], fontFamily: 'MaterialIcons'),      Color(json['colorIcon']),      Color(json['backGroundColor']),    );  }  // Method to convert CategoryModel to JSON  Map<String, dynamic> toJson() {    return {      'name': name,      'icon': icon.codePoint, // Using codePoint for IconData serialization      'colorIcon': colorIcon.value,      'backGroundColor': backGroundColor.value,    };  }  static List<CategoryModel> defaultCategory = [    CategoryModel("Grocery", Icons.cake_outlined, const Color(0xFF21A300),        const Color(0xFFCCFF80)),    CategoryModel("Work", Icons.badge_outlined, const Color(0xFFA31D00),        const Color(0xFFFF9680)),    CategoryModel("Sport", Icons.sports, const Color(0xFF00A32F),        const Color(0xFF80FFFF)),    CategoryModel("Design", Icons.draw_outlined, const Color(0xFF80FFD9),        const Color(0xFF00A372)),    CategoryModel("University", Icons.school_outlined, const Color(0xFF0055A3),        const Color(0xFF809CFF)),    CategoryModel("Social", Icons.sensor_occupied_outlined,        const Color(0xFFA30089), const Color(0xFFFF80EB)),    CategoryModel("Music", Icons.music_note_outlined, const Color(0xFFA000A3),        const Color(0xFFFC80FF)),    CategoryModel("Health", Icons.monitor_heart_outlined,        const Color(0xFF00A3A3), const Color(0xFF80FFA3)),    CategoryModel("Movie", Icons.videocam_outlined, const Color(0xFF0069A3),        const Color(0xFF80D1FF)),    CategoryModel("Home", Icons.house_outlined, const Color(0xFFA30000),        const Color(0xFFFF8080)),  ];}