import 'package:uptodo/features/Calendar/data/models/priority_model.dart';import 'category_model.dart';// class TaskModel {//   final String title;//   final String description;//   final DateTime dateTime;//   bool isCompleted = false;//   final CategoryModel type;//   final PriorityModel priority;////   TaskModel(//       this.title, this.dateTime, this.type, this.priority, this.description);// }class TaskModel {  final String title;  final String description;  final DateTime dateTime;  bool isCompleted;  final CategoryModel type;  final PriorityModel priority;  TaskModel(    this.title,    this.dateTime,    this.type,    this.priority,    this.description, {    this.isCompleted = false, // Default value for isCompleted  });  // Method to convert TaskModel to JSON  Map<String, dynamic> toJson() {    return {      'title': title,      'description': description,      'dateTime': dateTime.toIso8601String(),      'isCompleted': isCompleted,      'type': type.toJson(), // Assuming CategoryModel has a toJson method      'priority':          priority.toJson(), // Assuming PriorityModel has a toJson method    };  }  // Factory method to create TaskModel from JSON  factory TaskModel.fromJson(Map<String, dynamic> json) {    return TaskModel(      json['title'],      DateTime.parse(json['dateTime']),      CategoryModel.fromJson(          json['type']), // Assuming CategoryModel has a fromJson method      PriorityModel.fromJson(          json['priority']), // Assuming PriorityModel has a fromJson method      json['description'],      isCompleted: json['isCompleted'] ?? false,    );  }}