import 'package:flutter/material.dart';import '../../../../core/utils/resource/string.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';class BuildButtonsSwitch extends StatefulWidget {  const BuildButtonsSwitch({    super.key,  });  @override  State<BuildButtonsSwitch> createState() => _BuildButtonsSwitchState();}double _heightBigContainer = .12;double _heightContainers = .07;double _widthContainers = .4;BoxBorder _borderContainer =    Border.all(width: 2, color: AppColors.gray2TextColor);bool isSelected = false;class _BuildButtonsSwitchState extends State<BuildButtonsSwitch> {  @override  Widget build(BuildContext context) {    return Container(      margin: const EdgeInsets.only(        left: 25,        right: 25,        top: 20,      ),      color: AppColors.secondBackGroundColor,      height: MediaQuery.sizeOf(context).height * _heightBigContainer,      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceAround,        children: [          InkWell(              onTap: () {                setState(() {                  isSelected = false;                });              },              child: Container(                height: MediaQuery.sizeOf(context).height * _heightContainers,                width: MediaQuery.sizeOf(context).width * _widthContainers,                decoration: BoxDecoration(                  color:                      isSelected ? Colors.transparent : AppColors.primaryColor,                  border: isSelected ? _borderContainer : null,                ),                child: Align(                    child: Text(                  AppStrings.todayTask,                  style: Styles.text16(),                )),              )),          InkWell(            onTap: () {              setState(() {                isSelected = true;              });            },            child: Container(              height: MediaQuery.sizeOf(context).height * _heightContainers,              width: MediaQuery.sizeOf(context).width * _widthContainers,              decoration: BoxDecoration(                color: isSelected ? AppColors.primaryColor : Colors.transparent,                border: !isSelected ? _borderContainer : null,              ),              child: Align(                  child: Text(                AppStrings.completeTask,                style: Styles.text16(),              )),            ),          ),        ],      ),    );  }}