import 'package:easy_date_timeline/easy_date_timeline.dart';import 'package:flutter/material.dart';import 'package:intl/intl.dart';import '../../../../core/utils/resource/icons.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';TextStyle _dayNumStyle = Styles.text14();TextStyle _dayStrStyle = Styles.text12().copyWith(fontWeight: FontWeight.w700);BorderRadiusGeometry _borderRadius = const BorderRadius.all(Radius.circular(4));class BuildDateTimeLine extends StatelessWidget {  const BuildDateTimeLine({    super.key,  });  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.only(        top: 15,      ),      color: AppColors.secondBackGroundColor,      child: Column(        children: [          Row(            mainAxisAlignment: MainAxisAlignment.spaceAround,            children: [              AppIcons.arrowBack,              Column(                children: [                  Text(                    DateFormat('MMMM').format(DateTime.now()).toUpperCase(),                    style:                        Styles.text14().copyWith(fontWeight: FontWeight.w400),                  ),                  Text(                    DateFormat('yyyy').format(DateTime.now()),                    style: Styles.text12()                        .copyWith(color: AppColors.secondTextColor),                  ),                ],              ),              AppIcons.arrowForward            ],          ),          EasyDateTimeLine(            timeLineProps:                const EasyTimeLineProps(separatorPadding: 11, vPadding: 10),            initialDate: DateTime.now(),            onDateChange: (selectedDate) {},            headerProps: const EasyHeaderProps(showHeader: false),            dayProps: EasyDayProps(              height: MediaQuery.sizeOf(context).height * .09,              width: MediaQuery.sizeOf(context).width / 9,              borderColor: Colors.transparent,              todayStyle: DayStyle(                borderRadius: 4,                dayNumStyle: _dayNumStyle,                dayStrStyle: _dayStrStyle,              ),              inactiveDayStyle: DayStyle(                decoration: BoxDecoration(                  color: AppColors.backGroundColorItemsCalender,                  borderRadius: _borderRadius,                ),                dayNumStyle: _dayNumStyle,                dayStrStyle: _dayStrStyle,              ),              activeDayStyle: DayStyle(                decoration: BoxDecoration(                  color: AppColors.primaryColor,                  borderRadius: _borderRadius,                ),                dayNumStyle: _dayNumStyle,                dayStrStyle: _dayStrStyle,              ),              dayStructure: DayStructure.dayStrDayNum,              todayHighlightStyle: TodayHighlightStyle.withBackground,            ),          ),        ],      ),    );  }} //resource