import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:intl/intl.dart';import '../../data/models/task_model.dart';import '../../../../core/utils/resource/icons.dart';import '../manager/calendar_cubit.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';class CardItemTask extends StatelessWidget {  final TaskModel state;  const CardItemTask({    super.key,    required this.state,  });  @override  Widget build(BuildContext context) {    return Card(      margin: const EdgeInsets.only(left: 24, right: 24, top: 17),      shape: const ContinuousRectangleBorder(),      color: AppColors.secondBackGroundColor,      child: ListTile(        leading: IconButton(            onPressed: () {              state.isCompleted = !state.isCompleted;              context.read<CalendarCubit>().filter();            },            icon: !state.isCompleted ? AppIcons.circle : AppIcons.checkCircle),        title: Text(state.title, style: Styles.text16()),        subtitle: Row(          crossAxisAlignment: CrossAxisAlignment.start,          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            Text(              "Today At ${DateFormat("E d").format(state.dateTime)}",              style: Styles.text14().copyWith(color: AppColors.gray1TextColor),            ),            Row(              crossAxisAlignment: CrossAxisAlignment.end,              children: [                Container(                  padding: const EdgeInsets.symmetric(horizontal: 8),                  color: state.type.backGroundColor,                  child: Row(                    children: [                      Padding(                        padding: const EdgeInsets.all(3.0),                        child: Icon(                          state.type.icon,                          color: state.type.colorIcon,                        ),                      ),                      Text(                        state.type.name,                        style: Styles.text12(),                      )                    ],                  ),                ),                const SizedBox(                  width: 12,                ),                Container(                  padding:                      const EdgeInsets.symmetric(horizontal: 8, vertical: 3),                  decoration: BoxDecoration(                    color: Colors.transparent,                    border: Border.all(width: 1, color: AppColors.primaryColor),                  ),                  child: Row(                    children: [                      Padding(                        padding: const EdgeInsets.symmetric(horizontal: 3.0),                        child: Icon(state.priority.flag, color: Colors.white),                      ),                      Text(                        state.priority.number.toString(),                        style: Styles.text12(),                      )                    ],                  ),                ),              ],            )          ],        ),      ),    );  }}