import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/utils/resource/constant.dart';import '../../ViewModel/GetTask/get_task_cubit.dart';class CustomSearchTextField extends StatefulWidget {  const CustomSearchTextField({super.key});  @override  State<CustomSearchTextField> createState() => _CustomSearchTextFieldState();}class _CustomSearchTextFieldState extends State<CustomSearchTextField> {  late TextEditingController search = TextEditingController();  @override  void initState() {    search.clear();    super.initState();    // search = TextEditingController();  }  @override  void dispose() {    search.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Hero(        tag: AppConstant.tagSearch,        child: Padding(          padding:              const EdgeInsets.only(top: 16, bottom: 20, right: 24, left: 24),          child: TextFormField(            onChanged: (query) {              context.read<GetTaskCubit>().searchTasksByTitle(query);            },            controller: search,            decoration: InputDecoration(              enabledBorder: buildOutlineInputBorder(),              focusedBorder: buildOutlineInputBorder(),              hintText: 'Search For Your Task ...',              prefixIcon: IconButton(                onPressed: () {},                icon: IconButton(                    padding: EdgeInsets.zero,                    onPressed: () {},                    icon: const Icon(                      Icons.search_rounded,                    )),              ),            ),          ),        ));  }  OutlineInputBorder buildOutlineInputBorder() {    return OutlineInputBorder(      borderSide: const BorderSide(        color: Colors.grey,      ),      borderRadius: BorderRadius.circular(        0,      ),    );  }}