import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:uptodo/features/Index/View/Pages/task_is_today.dart';import 'package:uptodo/features/Index/View/Pages/task_onther_day.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/size.dart';import '../../../../core/utils/resource/string.dart';import '../../../User/ViewModel/user_cubit.dart';import '../Widget/custom_search_text_field.dart';class IndexScreen extends StatelessWidget {  const IndexScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Container(            margin: EdgeInsets.only(top: 13),            child: Text(AppStrings.indexNamePage)),        leading: Container(            margin: EdgeInsets.only(left: 24, top: 13),            child: Icon(              Icons.sort_rounded,              color: AppColors.iconColor,            )),        actions: [          Container(            margin: EdgeInsets.only(right: 24, top: 13),            child: CircleAvatar(              backgroundImage: context.watch<UserCubit>().imageUser == null                  ? NetworkImage(                      context.watch<UserCubit>().initUserScreen().image)                  : FileImage(context.watch<UserCubit>().imageUser!),              radius: AppSize(context).percentageWidth(.05),            ),          )        ],      ),      body: Column(        children: [          CustomSearchTextField(),          // Expanded(child: TaskIsToday()),          Expanded(child: TaskOntherDay()),        ],      ),    );  }}