import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/utils/resource/constant.dart';import '../Widget/task_running_today.dart';import '../Widget/task_completed_today.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/string.dart';import '../../../User/ViewModel/user_cubit.dart';import '../Widget/custom_search_text_field.dart';class IndexScreen extends StatelessWidget {  const IndexScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Container(          margin: EdgeInsets.only(top: 13).r,          child: Text(            AppStrings.indexNamePage,          ),        ),        leading: Container(          margin: EdgeInsets.only(left: 24, top: 13).r,          child: Icon(            Icons.sort_rounded,            color: AppColors.iconColor,          ),        ),        actions: [          Container(            margin: EdgeInsets.only(right: 24, top: 13).r,            child: CircleAvatar(              backgroundImage: context.watch<UserCubit>().imageUser == null                  ? NetworkImage(                      context.watch<UserCubit>().initUserScreen().image,                    )                  : FileImage(                      context.watch<UserCubit>().imageUser!,                    ),              radius: (.05).sw,            ),          )        ],      ),      body: CustomScrollView(        shrinkWrap: true,        physics: AppConstant.physics,        slivers: [          SliverToBoxAdapter(            child: CustomSearchTextField(),          ),          SliverToBoxAdapter(            child: TaskIsToday(),          ),          SliverToBoxAdapter(            child: TaskCompletedToday(),          ),        ],      ),    );  }}