import 'package:flutter/material.dart';import 'package:uptodo/features/Index/View/Pages/search_screen.dart';import '../../../../core/utils/resource/constant.dart';import '../Widget/app_bar_index.dart';import '../Widget/task_running_today.dart';import '../Widget/task_completed_today.dart';import 'custom_search_text_field.dart';class IndexScreen extends StatelessWidget {  const IndexScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: BuildAppBarIndex(),      body: CustomScrollView(        shrinkWrap: true,        physics: AppConstant.physics,        slivers: [          SliverToBoxAdapter(            child: MaterialButton(                onPressed: () {                  Navigator.of(context,rootNavigator: false).push(                    MaterialPageRoute(                      builder: (context) => SearchScreen(),                    ),                  );                },                child: CustomSearchTextField()),          ),          SliverToBoxAdapter(            child: TaskIsToday(),          ),          SliverToBoxAdapter(            child: TaskCompletedToday(),          ),        ],      ),    );  }}