import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:uptodo/features/TaskManagement/ViewModel/AddTask/add_task_cubit.dart';import '../../../../core/utils/Widget/build_task.dart';class TaskIsToday extends StatefulWidget {  const TaskIsToday({super.key});  @override  State<TaskIsToday> createState() => _TaskIsTodayState();}class _TaskIsTodayState extends State<TaskIsToday> {  @override  Widget build(BuildContext context) {    return BlocBuilder<AddTaskCubit, AddTaskState>(      builder: (context, state) {        return ListView.builder(          itemBuilder: (context, index) => CardItemTask(            state: context.read<AddTaskCubit>().isTodayTask[index],          ),          itemCount: context.read<AddTaskCubit>().isTodayTask.length,        );      },    );  }}