import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/utils/Widget/build_task.dart';import '../../../TaskManagement/ViewModel/AddTask/add_task_cubit.dart';class TaskOntherDay extends StatefulWidget {  const TaskOntherDay({super.key});  @override  State<TaskOntherDay> createState() => _TaskOntherDayState();}class _TaskOntherDayState extends State<TaskOntherDay> {  @override  Widget build(BuildContext context) {    return BlocBuilder<AddTaskCubit, AddTaskState>(      builder: (context, state) {        return ListView.builder(          shrinkWrap: true,          physics: NeverScrollableScrollPhysics(),          itemBuilder: (context, index) => CardItemTask(            state: context.read<AddTaskCubit>().isCompleted[index],          ),          itemCount: context.read<AddTaskCubit>().isCompleted.length,        );      },    );  }}