import 'package:flutter/material.dart';class CustomSearchTextField extends StatefulWidget {  const CustomSearchTextField({super.key});  @override  State<CustomSearchTextField> createState() => _CustomSearchTextFieldState();}class _CustomSearchTextFieldState extends State<CustomSearchTextField> {  late TextEditingController search = TextEditingController();  @override  void initState() {    search.clear();    super.initState();  }  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(top: 16, bottom: 20, right: 24, left: 24),      child: TextFormField(        onChanged: (result) {          // SearchCubit.get(context).category = result;          // SearchCubit.get(context).search();        },        controller: search,        decoration: InputDecoration(          enabledBorder: buildOutlineInputBorder(),          focusedBorder: buildOutlineInputBorder(),          hintText: 'Search for your task...',          prefixIcon: IconButton(            onPressed: () {},            icon: IconButton(                padding: EdgeInsets.zero,                onPressed: () {},                icon: const Icon(                  Icons.search_rounded,                )),          ),        ),      ),    );  }  OutlineInputBorder buildOutlineInputBorder() {    return OutlineInputBorder(      borderSide: const BorderSide(        color: Colors.grey,      ),      borderRadius: BorderRadius.circular(        0,      ),    );  }}