import 'package:flutter/material.dart';class ListActions {  final String title;  final List<ItemModel> items;  ListActions({    required this.title,    required this.items,  });  factory ListActions.fromJson(Map<String, dynamic> json) {    return ListActions(      title: json['title'],      items: (json['items'] as List<dynamic>)          .map((itemJson) =>              ItemModel.fromJson(itemJson as Map<String, dynamic>))          .toList(),    );  }  Map<String, dynamic> toJson() {    return {      'title': title,      'items': items.map((item) => item.toJson()).toList(),    };  }}class ItemModel {  final String title;  final IconData icon;  final Widget screen;  ItemModel({    required this.title,    required this.icon,    required this.screen,  });  factory ItemModel.fromJson(Map<String, dynamic> json) {    return ItemModel(      title: json['title'],      icon: IconData(        json['icon']['codePoint'],        fontFamily: json['icon']['fontFamily'],        fontPackage: json['icon']['fontPackage'],        matchTextDirection: json['icon']['matchTextDirection'] ?? false,      ),      screen: Container(),    );  }  Map<String, dynamic> toJson() {    return {      'title': title,      'icon': {        'codePoint': icon.codePoint,        'fontFamily': icon.fontFamily,        'fontPackage': icon.fontPackage,        'matchTextDirection': icon.matchTextDirection,      },    };  }}