import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/utils/resource/constant.dart';import '../../../../core/utils/resource/list.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/icons.dart';import '../../../../core/utils/resource/styles.dart';import '../../Model/profile_item_model.dart';class BoxOFListProfileItems extends StatelessWidget {  const BoxOFListProfileItems({    super.key,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 24).r,      child: ListView.builder(        physics: AppConstant.physics,        shrinkWrap: true,        itemCount: AppList.listActions.length,        itemBuilder: (context, index) {          return Padding(            padding: const EdgeInsets.symmetric(vertical: 8.0).r,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Text(                  AppList.listActions[index].title,                  style: Styles.text14().copyWith(                    color: AppColors.gray1TextColor,                  ),                ),                8.verticalSpace,                ListView.builder(                  physics: AppConstant.physics,                  shrinkWrap: true,                  itemCount: AppList.listActions[index].items.length,                  itemBuilder: (context, indexItem) => _BuildListProfileItems(                    itemModel: AppList.listActions[index].items[indexItem],                  ),                ),              ],            ),          );        },      ),    );  }}class _BuildListProfileItems extends StatelessWidget {  final ItemModel itemModel;  const _BuildListProfileItems({    required this.itemModel,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 8.0).r,      child: Container(        padding: const EdgeInsets.symmetric(vertical: 8.0).r,        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            Row(              children: [                Icon(                  itemModel.icon,                  color: AppColors.iconColor,                ),               10.horizontalSpace,                Text(                    itemModel.title,                    maxLines: 1,                    style: Styles.text16(),                  ),              ],            ),            InkWell(              onTap: () {                showAdaptiveDialog(                  context: context,                  builder: (context) {                    return itemModel.screen;                  },                );              },              child: AppIcons.arrowForward,            ),          ],        ),      ),    );  }}