import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/utils/resource/colors.dart';class BuildUiSettingAccount extends StatelessWidget {  const BuildUiSettingAccount({    super.key,    this.screenHeightItemModel,    this.screenWidthItemModel,    required this.itemModel,    this.screenMarginBottom,  });  final double? screenHeightItemModel;  final double? screenWidthItemModel;  final double? screenMarginBottom;  final Widget itemModel;  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsets.only(bottom: screenMarginBottom ?? 25),      child: Dialog(          backgroundColor: AppColors.secondBackGroundColor,          shape: ContinuousRectangleBorder(),          alignment: Alignment.bottomCenter,          insetPadding: EdgeInsets.zero,          child: Container(            decoration: BoxDecoration(              borderRadius: BorderRadius.circular(12.0).r,            ),            height: (screenHeightItemModel ?? 200).h,            width: screenWidthItemModel ?? (327).w,            margin: EdgeInsets.zero,            child: itemModel,          )),    );  }}