import 'package:flutter/material.dart';import '../../../../core/utils/resource/colors.dart';class BuildUiSettingAccount extends StatelessWidget {  const BuildUiSettingAccount({    super.key,    this.screenHeightItemModel,    this.screenWidthItemModel,    required this.itemModel,    this.screenMarginBottom,  });  final double? screenHeightItemModel;  final double? screenWidthItemModel;  final double? screenMarginBottom;  final Widget itemModel;  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsets.only(bottom: screenMarginBottom ?? 28),      child: Dialog(          backgroundColor: AppColors.secondBackGroundColor,          shape: ContinuousRectangleBorder(),          alignment: Alignment.bottomCenter,          insetPadding: const EdgeInsets.all(0),          child: Container(            decoration: BoxDecoration(              borderRadius: BorderRadius.circular(12.0),            ),            height: MediaQuery.of(context).size.height *                (screenHeightItemModel ?? 0.25),            width: MediaQuery.of(context).size.width *                (screenWidthItemModel ?? .9),            margin: const EdgeInsets.all(0),            child: itemModel,          )),    );  }}